import React, {useContext, useState, useEffect} from "react";
import { useParams } from "react-router";

import styled from "styled-components";

import { ItemsContext } from "./ItemsContext";

const ItemDetails = () => {
    const {_id} = useParams();

    //consuming array where we gonna store items, that we are addin to the cart
    const {cartItems, setCartItems} = useContext(ItemsContext);

    console.log(cartItems)

    //variable to store fetch result
    const [item, setItem] = useState(null);

    //variable to store number of items user wants to put to the cart
    const [quantity, setQuantity] = useState(1);

    //setting up an object of chosen item's _id with it's quantity
    const [itemToAdd, setItemToAdd] = useState({});
    console.log(itemToAdd, "itemToAdd")

    useEffect(() => {
        fetch(`/items/${_id}`)
        .then(res => res.json())
        .then(data => setItem(data.data))
        .catch((err) => {
            console.log("error");
        });
    }, []);

    //decreases num of items by 1
    const handleDecrement = () => {
        if (quantity > 0)
        setQuantity(quantity - 1)
    }

    //increases num of items by 1
    const handleIncrement = () => {
        if (quantity <= item.numInStock - 1)
        setQuantity(quantity + 1)
    }

    //storing item id with its quantity in "itemToAdd", 
    //pushing it into array "cartItems", where we store all items user picks
    //saving "itemToAdd" object in a localStorage
    const handleAddToCart = () => {
        setItemToAdd({
            productId: item._id,
            quantity: quantity,
        })
        cartItems.push(itemToAdd)
        // window.localStorage.setItem("item", JSON.stringify(itemToAdd))
    }

    return(
        <>
        {
            item && (
                <Wrapper>
                    <Card>
                        <Image src={item.imageSrc}/>
                        <Info>
                            <Name>{item.name}</Name>
                            <InStock>In stock: {item.numInStock}</InStock>
                            <Price>{item.price}</Price>
                            <Quantity>
                                <Button onClick={handleDecrement}>-</Button><Number>{quantity}</Number><Button onClick={handleIncrement}>+</Button>
                            </Quantity>
                            {
                                quantity === item.numInStock + 1 || quantity === 0 ? (
                                    <Disabled>
                                        Add to card
                                    </Disabled>
                                ) : (
                                    <Btn onClick={handleAddToCart}>
                                        <Span1></Span1>
                                        <Span2></Span2>
                                        <Span3></Span3>
                                        <Span4></Span4>
                                        Add to card
                                    </Btn>                        
                                )
                            }
                        </Info>
                    </Card>
                </Wrapper>
            )
        }
        </>
    )
}

