import React from "react";
import styled from "styled-components";

import { ItemsContext } from "./ItemsContext.js";
import Pagination from "./Pagination.js";
import PageNav from "./PageNav.js";

const HomePage = () => {
  const { items, setItems } = React.useContext(ItemsContext);

  return (
    items && (
      <Wrapper>
        {/* pagination is the page with limites item display, I set it to 24 */}
        <Pagination />
        {/* PageNav is the navigation of page 1 2 3 4 .... */}
        <PageNav totalItems={items.length} />
      </Wrapper>
    )
  );
};
const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

export default HomePage;
