import React from "react";
import styled from "styled-components";
import { NavLink } from "react-router-dom";
import { PageContext } from "./PageContext";
import { ItemsContext } from "./ItemsContext.js";

const PageNav = ({ totalItems }) => {
  const { itemsPerPage, setItemsPerPage, currentPage, setCurrentPage } =
    React.useContext(PageContext);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };
  // to calculate how many pages we need and push it in array
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(totalItems / itemsPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <nav>
      <Wrapper>
        {/* map the array to make a list */}
        {pageNumbers.map((number) => (
          <li key={number} className="page-item">
            <a onClick={() => paginate(number)} className="page-link">
              {number}
            </a>
          </li>
        ))}
      </Wrapper>
    </nav>
  );
};
const Wrapper = styled.ul``;

export default PageNav;
