import React, { createContext, useState, useEffect } from "react";
import styled from "styled-components";
import { useHistory } from "react-router-dom";
import { ItemsContext } from "./ItemsContext.js";

const ItemCard = ({ item }) => {
  const { cartItems, setCartItems } = React.useContext(ItemsContext);

  const history = useHistory();
  const [itemToAdd, setItemToAdd] = useState({});
  const [quantity, setQuantity] = useState(1);
  const handleItemDetails = (e) => {
    if (e.key === "Enter" || e.type === "click") {
      history.push(`/item/${item._id}`);
    }
  };

  const handleAddCart = (e) => {
    e.stopPropagation();

    setQuantity(quantity + 1);
    console.log(quantity);
    if (item.numInStock < quantity + 1) {
      window.alert("not enough");
    } else {
      let itemAdded = {
        ...item,
        quantity: quantity,
      };
      cartItems.push(itemAdded);
      setCartItems(cartItems);
    }

    // console.log(itemAdded);
  };
  // console.log(cartItems);
  return (
    <Wrapper onClick={handleItemDetails}>
      <Img src={item.imageSrc} />
      <Info>
        <Name>{item.name}</Name>
        <Price>{item.price}</Price>
        <Btn onClick={handleAddCart}>
          <Span1></Span1>
          <Span2></Span2>
          <Span3></Span3>
          <Span4></Span4>
          Add to Cart
        </Btn>
        {/* <AddCartButton onClick={handleAddCart}>Add to Cart</AddCartButton> */}
      </Info>
    </Wrapper>
  );
};
const Wrapper = styled.div`
