import React, { useState, useContext } from "react";
import styled from "styled-components";
import { ItemsContext } from "./ItemsContext";
import { useHistory } from "react-router-dom";

const SearchBar = () => {
  const [input, setInput] = useState("");
  const { items } = useContext(ItemsContext);
  const history = useHistory();

  const handleChange = (e) => {
    setInput(e.target.value);
  };
  const reset = () => {
    setInput("");
  };

  //   const handleKeyDown = (ev) => {
  //     switch (ev.key) {
  //       case "Enter": {
  //         history.push(`/item/${item._id}`);
  //       }
  //     }
  //   };
  const handleSelect = (itemId) => {
    history.push(`/item/${itemId}`);
    reset();
  };

  let matchedItems = [];
  if (input.length >= 2) {
    matchedItems = items.filter((suggestion) => {
      return suggestion.name.toLowerCase().includes(input.toLowerCase());
    });
    console.log(matchedItems);
  }

  return (
    <Wrapper>
      <Search>
        <SearchInput
          type="text"
          value={input}
          onChange={handleChange}
          //   onKeyDown={handleKeyDown}
        />
        {matchedItems.length >= 1 && (
          <SuggestList>
            {matchedItems.map((suggestion) => {
              let index = suggestion.name
                .toLowerCase()
                .indexOf(input.toLowerCase());

              let normalText = suggestion.name.slice(0, index + input.length);
              let boldText = suggestion.name.slice(index + input.length);
              return (
                <SuggestItem
                  key={suggestion._id}
                  onClick={() => handleSelect(suggestion._id)}
                >
                  {/* {suggestion.title} */}
                  <Span>
                    {normalText}
                    <Prediction>{boldText}</Prediction>
                  </Span>
                  <Category>
                    {" "}
                    in <Purple>{suggestion.category}</Purple>
                  </Category>
                </SuggestItem>
              );
            })}
          </SuggestList>
        )}
      </Search>
      <Clear onClick={reset}>Clear</Clear>
    </Wrapper>
  );
};

export default SearchBar;

const Wrapper = styled.div`
  display: flex;

  margin-top: 20px;
`;
const SearchInput = styled.input`
  background: var(--color-light-grey);
  padding: 10px;
  border-radius: 5px;
  border: none;
  width: 300px;
  font-size: 18px;
`;

const Search = styled.div`
  width: 400px;
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  padding: 10px 10px;
  margin: 8px;
  width: 300px;
  height: 40px;
  border: 1px solid #ccc;
  border-radius: 4px;
  outline-color: red;
`;
const SuggestList = styled.ul`
  margin: 8px 8px;
  border-radius: 5px;
  box-shadow: 0px 3px 10px rgb(234, 234, 234);
  width: 400px;
  padding: 10px;
`;
const SuggestItem = styled.li`
  padding: 10px 5px;
  cursor: pointer;

  &:hover,
  & :active {
    background-color: lightyellow;
  }
`;
const Span = styled.span`
  color: black;
`;

const Clear = styled.button`
  width: 80px;
  height: 35px;
  border-radius: 3px;
  padding: 10px 0;
  color: white;
  background-color: var(--color-lime);
  margin-top: 5px;
  margin-left: -70px;

  text-align: center;
  cursor: pointer;
`;
const Prediction = styled.span`
  font-weight: 600;
`;
const Category = styled.span`
  font-style: italic;
`;
const Purple = styled.span`
  color: rgb(152, 82, 159);
`;
