import React from "react";
import styled from "styled-components";
import {  useHistory } from "react-router-dom";

import { User, ShoppingCart, FileText } from "react-feather";

const NavBar = () => {
    const history = useHistory();

    const handleToHomePage = () => {
        history.push("/")
    }

    const handleToCart = () => {
        history.push("/cart")
    }

    // const handleToOrder = () => {
    //     history.push("/order")
    // }

    return(
        <Wrapper>
            <Logo onClick={handleToHomePage}>Best e-commerce site</Logo>
            <Icon >
                <User />
            </Icon>
            <Icon onClick={handleToCart}>
                {/* inside this span "NumItems" will be quantity of added items */}
                <ShoppingCart /><NumItems>1</NumItems>
            </Icon>
            {/* conditional rendering on this one only if there's order being stored in localStorage */}
            {/* <Icon onClick={handleToOrder}>
                <FileText />
            </Icon> */}
        </Wrapper>
    )
}

const Wrapper = styled.div`
    height: 80px;
    width: 100%;
    display: flex;
    justify-content: flex-end;
    background: var(--color-dark-grey);
    align-items: center;
`;
const Logo = styled.div`
    margin-right: auto;
    padding: 20px;
    font-size: 30px;
    text-transform: uppercase;
    font-weight: 700;
    color: var(--color-lime);

    &:hover{
        cursor: pointer;
        color: var(--color-neon-lime)
    }
`;
const Icon = styled.div`
    padding: 20px;
    color: var(--color-lime);

    &:hover{
        cursor: pointer;
        color: var(--color-neon-lime)
    }
`;
const NumItems = styled.span`
    margin: 5px;
`;
export default NavBar;
